// page init
jQuery(function(){
	initCarousel();
	initCycleCarousel();
	initMobileNav();
	initBackgroundResize();
	initInputs();
	initSameHeight();
	initFitVids();
	initBlockMovement();
	//initTouchNav();
	initHoverDrop();
});

function initHoverDrop() {
	var isTouchDevice = ('ontouchstart' in window) || window.DocumentTouch && document instanceof DocumentTouch || /Windows Phone/.test(navigator.userAgent);
	
	if (isTouchDevice) {
		return;
	}
	jQuery('#nav [data-toggle="dropdown"]').each(function() {
		var item = jQuery(this),
			parent = item.parent();

		parent.one('mousemove', function() {
			parent.hover(function() {
				parent.addClass('open');
			}, function() {
				parent.removeClass('open');
			}).trigger('mouseover');
		});
	});
}

// handle dropdowns on mobile devices
function initTouchNav() {
	jQuery('#nav').each(function(){
		new TouchNav({
			navBlock: this
		});
	});
}

// scroll gallery init
function initCarousel() {
	jQuery('div.gallery').scrollGallery({
		mask: 'div.mask',
		slider: 'div.slideset',
		slides: 'div.slide',
		btnPrev: 'a.btn-prev',
		btnNext: 'a.btn-next',
		pagerLinks: '.pagination li',
		handleTouch: false,
		autoRotation: false,
		switchTime: 3000,
		animSpeed: 500
	});
}

// Block Movement init
function initBlockMovement() {
	responsiveBlock({
		block: '.tab-holder',
		ranges: {
			'0..767': '.tabs-holder-box',
			'768..': '.tabs-holder-desktop'
		}
	});

	responsiveBlock({
		block: '.wheel-holder',
		ranges: {
			'0..767': '.wheel-box-holder',
			'768..': '.wheel-holder-box'
		}
	});
}

function responsiveBlock(options) {
	var block = jQuery(options.block);
	var range = options.ranges;
	var breakpoints = {};

	jQuery.each(range, function(key, elem) {
		breakpoints[key] = {
			on: function() {
				block.appendTo(elem);
			}
		};
	});

	ResponsiveHelper.addRange(breakpoints);
}


// cycle scroll gallery init
function initCycleCarousel() {
	jQuery('div.cycle-gallery').scrollAbsoluteGallery({
		mask: 'div.mask',
		slider: 'div.slideset',
		slides: 'div.slide-intro',
		btnPrev: 'a.btn-prev',
		btnNext: 'a.btn-next',
		pagerLinks: '.pagination li',
		stretchSlideToMask: true,
		pauseOnHover: true,
		autoRotation: true,
		switchTime: 5000,
		animSpeed: 500
	});
}

// mobile menu init
function initMobileNav() {
	jQuery('body').mobileNav({
		hideOnClickOutside: true,
		menuActiveClass: 'nav-active',
		menuOpener: '.mob-opener',
		menuDrop: '#nav .navbar-nav'
	});
}

// stretch background to fill blocks
function initBackgroundResize() {
	jQuery('.bg-stretch').each(function() {
		ImageStretcher.add({
			container: this,
			image: 'img'
		});
	});
}

// clear inputs on focus
function initInputs() {
	PlaceholderInput.replaceByOptions({
		// filter options
		clearInputs: true,
		clearTextareas: true,
		clearPasswords: true,
		skipClass: 'default',
		
		// input options
		wrapWithElement: true,
		showUntilTyping: false,
		getParentByClass: false,
		placeholderAttr: 'placeholder'
	});
}

// align blocks height
function initSameHeight() {
	jQuery('.row').sameHeight({
		elements: '.post-col',
		flexible: true,
		multiLine: true
	});
}

// handle flexible video size
function initFitVids() {
	jQuery('#content').fitVids();
}

